INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('1','Fin de la pobreza'	,'Para lograr este Objetivo de acabar con la pobreza, el crecimiento económico debe ser inclusivo, con el fin de crear empleos sostenibles y de promover la igualdad.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('2','Hambre cero'	,'El sector alimentario y el sector agrícola ofrecen soluciones claves para el desarrollo y son vitales para la eliminación del hambre y la pobreza.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('3','Salud y bienestar'	,'Para lograr los Objetivos de Desarrollo Sostenible, es fundamental garantizar una vida saludable y promover el bienestar universal.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('4','Educación de calidad'	,'La educación es la base para mejorar nuestra vida y el desarrollo sostenible.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('5','Igualdad de género'	,'La igualdad entre los géneros no es solo un derecho humano fundamental, sino la base necesaria para conseguir un mundo pacífico, próspero y sostenible.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('6','Agua limpia y saneamiento'	,'El agua libre de impurezas y accesible para todos es parte esencial del mundo en que queremos vivir.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('7','Energía asequible y no contaminante'	,'La energía es central para casi todos los grandes desafíos y oportunidades a los que se enfrenta el mundo en la actualidad.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('8','Trabajo decente y crecimiento económico'	,'Debemos reflexionar sobre este progreso lento y desigual, y revisar nuestras políticas económicas y sociales destinadas a erradicar la pobreza.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('9','Industria, innovación e infraestructuras'	,'Las inversiones en infraestructura son fundamentales para lograr un desarrollo sostenible.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('10','Reducción de las desigualdades'	,'Reducir la desigualdad en y entre los países.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('11','Ciudades y comunidades sostenibles'	,'Las inversiones en infraestructura son cruciales para lograr el desarrollo sostenible.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('12','Producción y consumo responsables'	,'El objetivo del consumo y la producción sostenibles es hacer más y mejores cosas con menos recursos.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('13','Acción por el clima'	,'El cambio climático es un reto global que no respeta las fronteras nacionales.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('14','Vida submarina'	,'Conservar y utilizar en forma sostenible los océanos, los mares y los recursos marinos para el desarrollo sostenible.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('15','Vida de ecosistemas terrestres'	,'Gestionar sosteniblemente los bosques, luchar contra la desertificación, detener e invertir la degradación de las tierras y detener la pérdida de biodiversidad.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('16','Paz, justicia e instituciones sólidas'	,'Acceso universal a la justicia y la construcción de instituciones responsables y eficaces a todos los niveles.');
INSERT INTO TBL_ODS(Objetivo, Nombre, Descripción) VALUES('17','Alianzas para lograr los objetivos'	,'Revitalizar la Alianza Mundial para el Desarrollo Sostenible.');
