## CREACION DE TABLA PARA PAISES drop table TBL_PAIS
CREATE TABLE TBL_PAIS(
   ID         INT(11) NOT NULL AUTO_INCREMENT
  ,NOMBRE     VARCHAR(80) 
  ,NAME       VARCHAR(80) 
  ,NOM        VARCHAR(42) 
  ,ISO2       VARCHAR(5) 
  ,ISO3       VARCHAR(5) 
  ,PHONE_CODE VARCHAR(11)
  ,PRIMARY KEY (ID)
);

## CREACION DE TABLA de ods DROP TABLE TBL_ODS
CREATE TABLE TBL_ODS(
   ID           INT(11) NOT NULL AUTO_INCREMENT
  ,Objetivo     VARCHAR(80) 
  ,Nombre       VARCHAR(80) 
  ,Descripción  VARCHAR(200) 
  ,PRIMARY KEY (ID)
);

## CREACION DE TABLA Ambito_VAR_Flow_ODS Codigo	Ambito_VAR_Flow	ODS principal
CREATE TABLE TBL_Ambito_VAR_Flow_ODS(
   ID              INT(11) NOT NULL AUTO_INCREMENT
  ,Codigo          VARCHAR(80) 
  ,Ambito_VAR_Flow VARCHAR(80) 
  ,ODS             INT
  ,PRIMARY KEY (ID)
);


